
// PDF.js worker - Enhanced production fallback
// This file will be used in production builds
// In development, we use the CDN version to avoid import issues

// For production, this would contain the actual PDF.js worker code
// For now, we'll redirect to a compatible CDN version
if (typeof importScripts !== 'undefined') {
  try {
    // Try the newer compatible version first
    importScripts('https://cdnjs.cloudflare.com/ajax/libs/pdf.js/4.11.47/pdf.worker.min.js');
  } catch (e) {
    console.error('Failed to load PDF.js worker from primary CDN:', e);
    
    // Fallback to older version
    try {
      importScripts('https://cdnjs.cloudflare.com/ajax/libs/pdf.js/4.0.379/pdf.worker.min.js');
    } catch (fallbackError) {
      console.error('Failed to load PDF.js worker from fallback CDN:', fallbackError);
    }
  }
}
